self

if headers = parse_headers(options)
  @headers.merge!(headers)
  @cookies.fill_from_headers(@headers)
end
if params = parse_params(options)
  @params.merge!(params)
end
if form = parse_form(options)
  @form.merge!(form)
end
if json = parse_json(options)
  @json.merge!(json)
end
if ssl = parse_ssl(options)
  @ssl = ssl
end
self

while REDIRECT_CODES.includes?(@response.status_code)
  @visited << "#{@request.verb} #{@request.uri}"
  if too_many_hops?
    raise(TooManyRedirectsError.new)
  end
  if endless_loop?
    raise(EndlessRedirectError.new)
  end
  @request = redirect_to(@response.headers["Location"]?)
  @response = yield @request
end
